import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  Avatar,
  Button,
  Paper,
  Grid,
  Typography,
  Container,
} from "@material-ui/core";
import { useHistory } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import ArrowBackIos from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIos from "@material-ui/icons/ArrowForwardIos";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";

import Icon from "./icon";
import { signin, signup } from "../../actions/auth";
import { AUTH } from "../../constants/actionTypes";
import useStyles from "./styles";
import Logo from "../../images/Logo-black.png";
import { motion } from "framer-motion";

const initialState = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: "",
};

const SignUp = () => {
  const [form, setForm] = useState(initialState);
  const [isSignup, setIsSignup] = useState(false);
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();

  const [showPassword, setShowPassword] = useState(false);
  const handleShowPassword = () => setShowPassword(!showPassword);

  const switchMode = () => {
    setForm(initialState);
    setIsSignup((prevIsSignup) => !prevIsSignup);
    setShowPassword(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isSignup) {
      dispatch(signup(form, history));
    } else {
      dispatch(signin(form, history));
    }
  };

  const googleSuccess = async (res) => {
    const result = res?.profileObj;
    const token = res?.tokenId;

    try {
      dispatch({ type: AUTH, data: { result, token } });

      history.push("/");
    } catch (error) {
      console.log(error);
    }
  };

  const googleError = () =>
    console.log("Google Sign In was unsuccessful. Try again later");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  return (
    <div className="auth">
      <Logo />
      <h1 className="auth__title">{isSignup ? "Sign up" : "Sign in"}</h1>
      <div className="auth__form">
        <form className={classes.form} onSubmit={handleSubmit}>
          <div className="auth__form__inputs">
            {isSignup && (
              <>
                <label htmlFor="firstName">First Name</label>
                <input
                  name="firstName"
                  id="firstName"
                  onChange={handleChange}
                  type="name"
                  className="auth__form__input"
                />
                <label htmlFor="lastName">Last Name</label>
                <input
                  name="lastName"
                  id="lastName"
                  onChange={handleChange}
                  type="name"
                  className="auth__form__input"
                />
              </>
            )}
            <label htmlFor="email">Email</label>
            <input
              name="email"
              id="email"
              onChange={handleChange}
              type="email"
              className="auth__form__input"
            />
            <label htmlFor="password">Password</label>
            {/* {showPassword ? (
              <VisibilityOff onClick={handleShowPassword} />
            ) : (
              <Visibility onClick={handleShowPassword} />
            )} */}
            <input
              name="password"
              id="password"
              onChange={handleChange}
              type={showPassword ? "text" : "password"}
              className="auth__form__input"
            />
            {isSignup && (
              <>
                <label htmlFor="confirmPassword">Confirm Password</label>
                <input
                  name="confirmPassword"
                  id="confirmPassword"
                  onChange={handleChange}
                  className="auth__form__input"
                />
              </>
            )}
          </div>
          <button type="submit" className="auth__btn">
            {isSignup ? "Sign Up" : "Sign In"}
          </button>
          {/* <GoogleLogin
            clientId="530455552066-t74oerumbdhmpqha68knr3aght147ift.apps.googleusercontent.com"
            render={(renderProps) => (
              <Button
                className={classes.googleButton}
                color="primary"
                fullWidth
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
                startIcon={<Icon />}
                variant="contained"
              >
                Google Sign In
              </Button>
            )}
            onSuccess={googleSuccess}
            onFailure={googleError}
            cookiePolicy="single_host_origin"
          /> */}
          <div className="auth__text">
            {/* <p onClick={switchMode}>
              {isSignup
                ? "Already have an account? Sign in"
                : "Don't have an account? Sign Up"}
            </p> */}
            {isSignup ? (
              <ArrowBackIos onClick={switchMode} />
            ) : (
              <ArrowForwardIos onClick={switchMode} />
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
