import React, { useState, useEffect } from "react";

import { Link, useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import decode from "jwt-decode";
import * as actionType from "../../constants/actionTypes";

import { motion, useCycle } from "framer-motion";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSignOutAlt,
  faSignInAlt,
  faLaptopCode,
} from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs } from "@fortawesome/free-brands-svg-icons";

import Toggle from "./Toggle";

const Navbar = () => {
  const [isOpen, toggleOpen] = useCycle(false, true);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
  const dispatch = useDispatch();
  const history = useHistory();

  const logout = () => {
    dispatch({ type: actionType.LOGOUT });

    history.push("/auth");

    setUser(null);
  };

  return (
    <motion.div
      initial={false}
      animate={isOpen ? "open" : "closed"}
      className={isOpen ? "navbar-open" : "navbar-closed"}
    >
      <Toggle toggle={() => toggleOpen()} className="navbar__togle-component" />
      {isOpen ? (
        <div className="navbar__content">
          <div className="navbar__links">
            {user?.result ? (
              <div className="home__content">
                <h2 className="home__name">{user?.result.name}</h2>
                <div className="home__authBtn">
                  <button onClick={logout}>Logout</button>
                </div>
              </div>
            ) : (
              <Link to="/auth" className="home__authBtn">
                <button>Sign In / Sign Up</button>
              </Link>
            )}
          </div>
        </div>
      ) : null}
    </motion.div>
  );
};

export default Navbar;
