import React, { useEffect } from "react";
import {
  Paper,
  Typography,
  CircularProgress,
  Divider,
} from "@material-ui/core/";
import { useDispatch, useSelector } from "react-redux";
import moment from "moment";
import { useParams, useHistory } from "react-router-dom";

import { getPost, getPostsBySearch } from "../../actions/posts";
import noImage from "../../images/noImage-2.jpg";
import useStyles from "./styles";

const Post = () => {
  const { post, posts, isLoading } = useSelector((state) => state.posts);
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getPost(id));
  }, [id]);

  useEffect(() => {
    if (post) {
      dispatch(
        getPostsBySearch({ search: "none", tags: post?.tags.join(",") })
      );
    }
  }, [post]);

  if (!post) return null;

  const openPost = (_id) => history.push(`/posts/${_id}`);

  if (isLoading) {
    return (
      <Paper elevation={6} className={classes.loadingPaper}>
        <CircularProgress size="7em" />
      </Paper>
    );
  }

  const recommendedPosts = posts.filter(({ _id }) => _id !== post._id);

  return (
    <div>
      <div>
        <h3>{post.title}</h3>
        <h5>{post.tags.map((tag) => `#${tag} `)}</h5>
        <p>{post.message}</p>
        <h6>Created by: {post.name}</h6>
        <h6>{moment(post.createdAt).fromNow()}</h6>
      </div>
      <div className={classes.imageSection}>
        <img
          className={classes.media}
          src={post.selectedFile || noImage}
          alt={post.title}
        />
      </div>

      {!!recommendedPosts.length && (
        <div className={classes.section}>
          <h6>You might also like:</h6>
          <div>
            {recommendedPosts.map(
              ({ title, name, message, likes, selectedFile, _id }) => (
                <div onClick={() => openPost(_id)} key={_id}>
                  <h2>{title}</h2>
                  <h2>{name}</h2>
                  <h3>{message}</h3>
                  <h3>Likes: {likes.length}</h3>
                  <img src={selectedFile} width="200px" />
                </div>
              )
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Post;
