import React, { useState } from "react";

import { Link, useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import * as actionType from "../../constants/actionTypes";
import { faPlusCircle, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import { motion, useCycle } from "framer-motion";

import SearchBar from "../SearchBar";

import Toggle from "./Toggle";

const Navbar = () => {
  const [isOpen, toggleOpen] = useCycle(false, true);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
  const dispatch = useDispatch();
  const history = useHistory();
  const [open, setOpen] = useState(false);

  const logout = () => {
    dispatch({ type: actionType.LOGOUT });

    history.push("/auth");

    setUser(null);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <motion.div
      initial={false}
      animate={isOpen ? "open" : "closed"}
      className={isOpen ? "navbar-open" : "navbar-closed"}
    >
      <Toggle toggle={() => toggleOpen()} className="navbar__togle-component" />
      {isOpen ? (
        <div className="navbar__content">
          <SearchBar />
          <div>
            {user?.result ? (
              <div className="navbar__content">
                <h2 className="navbar__name">{user?.result.name}</h2>
                <div className="navbar__authBtn">
                  <button onClick={logout}>Logout</button>
                </div>
                <FontAwesomeIcon
                  icon={faPlusCircle}
                  onClick={handleOpen}
                  className="openModal"
                />

                <Modal
                  aria-labelledby="spring-modal-title"
                  aria-describedby="spring-modal-description"
                  open={open}
                  onClose={handleClose}
                  className="modal"
                  closeAfterTransition
                  BackdropComponent={Backdrop}
                  BackdropProps={{
                    timeout: 500,
                  }}
                >
                  <Fade in={open}>
                    <div className="paper">
                      <FontAwesomeIcon icon={faTimes} onClick={handleClose} />
                      <Form currentId={currentId} setCurrentId={setCurrentId} />
                    </div>
                  </Fade>
                </Modal>
              </div>
            ) : (
              <Link to="/auth" className="navbar__authBtn">
                <button>Sign In / Sign Up</button>
              </Link>
            )}
          </div>
        </div>
      ) : null}
    </motion.div>
  );
};

export default Navbar;
