import React, { useEffect } from "react";

import { BeatLoader } from "react-spinners";
import { useDispatch, useSelector } from "react-redux";
import moment from "moment";
import { useParams, useHistory } from "react-router-dom";

import { getPost, getPostsBySearch } from "../../actions/posts";
import noImage from "../../images/noImage-2.jpg";

const Post = () => {
  const { post, posts, isLoading } = useSelector((state) => state.posts);
  const dispatch = useDispatch();
  const history = useHistory();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getPost(id));
  }, [id]);

  useEffect(() => {
    if (post) {
      dispatch(
        getPostsBySearch({ search: "none", tags: post?.tags.join(",") })
      );
    }
  }, [post]);

  if (!post) return null;

  const openPost = (_id) => history.push(`/posts/${_id}`);

  if (isLoading) {
    return <BeatLoader />;
  }

  const recommendedPosts = posts.filter(({ _id }) => _id !== post._id);

  return (
    <div className="postDetails__container">
      <div className="postDetails__content">
        <img src={post.selectedFile || noImage} className="postDetails__img" />
        <h2>Created by: {post.name}</h2>
        <div>{post.title}</div>
        <div>{post.tags.map((tag) => `#${tag} `)}</div>
        <h5>{moment(post.createdAt).fromNow()}</h5>
      </div>

      {!!recommendedPosts.length && (
        <div>
          <h6>You might also like:</h6>
          <div>
            {recommendedPosts.map(
              ({ title, name, message, likes, selectedFile, _id }) => (
                <div onClick={() => openPost(_id)} key={_id}>
                  <h2>{title}</h2>
                  <h2>{name}</h2>
                  <h3>{message}</h3>
                  <h3>Likes: {likes.length}</h3>
                  <img src={selectedFile} width="200px" />
                </div>
              )
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Post;
