import React, { useState, useEffect } from "react";

import { Link, useHistory, useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";
import decode from "jwt-decode";
import * as actionType from "../../constants/actionTypes";

import { motion, useCycle } from "framer-motion";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSignOutAlt,
  faSignInAlt,
  faLaptopCode,
} from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs } from "@fortawesome/free-brands-svg-icons";

import Toggle from "./Toggle";

const Navbar = () => {
  const [isOpen, toggleOpen] = useCycle(false, true);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();

  const logout = () => {
    dispatch({ type: actionType.LOGOUT });

    history.push("/auth");

    setUser(null);
  };
  useEffect(() => {
    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);

      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }

    setUser(JSON.parse(localStorage.getItem("profile")));
  }, [location]);
  return (
    <motion.div
      initial={false}
      animate={isOpen ? "open" : "closed"}
      className={isOpen ? "navbar-open" : "navbar-closed"}
    >
      <Toggle toggle={() => toggleOpen()} className="navbar__togle-component" />
      {isOpen ? (
        <div className="navbar__content">
          <div className="navbar__links">
            {user?.result ? (
              <div>
                <h2 className="navbar__name">{user?.result.name}</h2>
                <div className="navbar__authIcon">
                  <FontAwesomeIcon icon={faSignOutAlt} onClick={logout} />
                </div>
              </div>
            ) : (
              <Link to="/auth" className="navbar__authIcon">
                <FontAwesomeIcon icon={faSignInAlt} />
              </Link>
            )}
          </div>
          <div className="navbar__footer">
            <h3>Made with </h3>
            <FontAwesomeIcon icon={faLaptopCode} />
            <FontAwesomeIcon icon={faReact} />
            <FontAwesomeIcon icon={faJs} />
          </div>
        </div>
      ) : null}
    </motion.div>
  );
};

export default Navbar;
